import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const badgeVariants = cva(
  "inline-flex max-w-full shrink-0 items-center justify-center gap-1 whitespace-nowrap rounded-[4px] border border-transparent px-[6px] py-[2px] text-xs font-medium leading-none no-underline transition-[color,box-shadow] overflow-hidden [&_svg]:pointer-events-none [&_svg]:inline-block [&_svg]:size-3 [&_svg]:shrink-0 [&_svg]:align-middle focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive",
  {
    variants: {
      variant: {
        default: "bg-badge-default text-badge-default-foreground border-badge-default-border [a&]:hover:bg-badge-default/90",
        secondary:
          "bg-secondary text-secondary-foreground [a&]:hover:bg-secondary/90",
        destructive:
          "bg-destructive text-white [a&]:hover:bg-destructive/90 focus-visible:ring-destructive/20 dark:focus-visible:ring-destructive/40 dark:bg-destructive/60",
        outline:
          "border-border text-foreground [a&]:hover:bg-accent [a&]:hover:text-accent-foreground",
        ghost: "[a&]:hover:bg-accent [a&]:hover:text-accent-foreground",
        link: "text-primary underline-offset-4 [a&]:hover:underline",
        info: "bg-badge-info text-badge-info-foreground border-badge-info-border [a&]:hover:bg-badge-info/90",
        positive: "bg-badge-positive text-badge-positive-foreground border-badge-positive-border [a&]:hover:bg-badge-positive/90",
        negative: "bg-badge-negative text-badge-negative-foreground border-badge-negative-border [a&]:hover:bg-badge-negative/90",
        warning: "bg-badge-warning text-badge-warning-foreground border-badge-warning-border [a&]:hover:bg-badge-warning/90",
        urgent: "bg-badge-urgent text-badge-urgent-foreground border-badge-urgent-border [a&]:hover:bg-badge-urgent/90",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
)

function Badge({
  className,
  variant = "default",
  asChild = false,
  ...props
}: React.ComponentProps<"span"> &
  VariantProps<typeof badgeVariants> & { asChild?: boolean }) {
  const Comp = asChild ? Slot : "span"

  return (
    <Comp
      data-slot="badge"
      data-variant={variant}
      className={cn(badgeVariants({ variant }), className)}
      {...props}
    />
  )
}

export { Badge, badgeVariants }

banner.source:
"use client";

import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";
import { CircleAlertIcon, InfoIcon, XIcon } from "lucide-react";
import { toast } from "sonner";

import { cn } from "@/lib/utils";

const bannerVariants = cva(
  "relative flex w-full items-start gap-3 rounded-[3.5px] border px-4 py-3 text-sm [&_svg]:size-4 [&_svg]:shrink-0",
  {
    variants: {
      variant: {
        default:
          "border-banner-border bg-muted text-banner-text [&>svg]:text-banner-text dark:text-white",
        caution:
          "border-banner-border bg-amber-50 text-banner-text [&_[data-banner-icon]]:text-banner-text dark:border-banner-border dark:bg-amber dark:text-banner-text dark:[&_[data-banner-icon]]:text-banner-text",
        critical:
          "border-banner-border bg-banner-destructive/95 text-banner-text [&_[data-banner-icon]]:text-banner-text",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
);

function BannerIcon({
  variant,
}: {
  variant: "default" | "caution" | "critical";
}) {
  if (variant === "critical") {
    return <CircleAlertIcon data-banner-icon className="size-4 shrink-0" />;
  }
  return <InfoIcon data-banner-icon className="size-4 shrink-0" />;
}

export interface BannerAction {
  label: React.ReactNode;
  onClick: () => void;
}

export interface BannerProps
  extends
    Omit<React.HTMLAttributes<HTMLDivElement>, "title">,
    VariantProps<typeof bannerVariants> {
  title?: React.ReactNode;
  onClose?: () => void;
  closeLabel?: string;
  /** Optional action button (e.g. "Undo", "View") */
  action?: BannerAction;
}

function Banner({
  className,
  variant = "default",
  title,
  onClose,
  closeLabel = "Close",
  action,
  children,
  ...props
}: BannerProps) {
  return (
    <div
      data-slot="banner"
      data-variant={variant}
      role="alert"
      className={cn(
        bannerVariants({ variant }),
        (onClose || action) && "pe-10",
        className,
      )}
      {...props}
    >
      <BannerIcon variant={variant || "default"} />
      <div className="flex-1 min-w-0">
        {title && <p className="font-medium text-[#30313D] leading-none mb-1">{title}</p>}
        {children && <p className="leading-snug">{children}</p>}
        {action && (
          <button
            type="button"
            onClick={action.onClick}
            className="mt-2 inline-flex h-8 items-center justify-center rounded-md border border-[#D7D7D0] bg-white px-3 text-sm font-medium text-[#30313D] shadow-sm transition-colors hover:bg-muted/80 focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 dark:border-[#3d3d45] dark:bg-[#27272a] dark:text-[#f4f4f5] dark:hover:bg-[#3f3f46]"
          >
            {action.label}
          </button>
        )}
      </div>
      {onClose && (
        <button
          type="button"
          aria-label={closeLabel}
          onClick={onClose}
          className="absolute top-3 end-3 rounded-md p-1 opacity-70 transition-opacity hover:opacity-100 focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 shrink-0 [&_svg]:text-current"
        >
          <XIcon className="size-4" />
        </button>
      )}
    </div>
  );
}

export { Banner, bannerVariants };

export type BannerVariant = NonNullable<
  VariantProps<typeof bannerVariants>["variant"]
>;

export interface BannerOptions {
  variant?: BannerVariant;
  title?: React.ReactNode;
  description?: React.ReactNode;
  action?: BannerAction;
}

/**
 * Show a persistent banner using the same toast system (Sonner).
 * Banners do not auto-dismiss and show a close button.
 */
function banner(
  message: React.ReactNode,
  options?: BannerOptions,
): string | number {
  const { variant = "default", title, description, action } = options ?? {};
  return toast.custom(
    (id) => (
      <Banner
        variant={variant}
        title={title}
        onClose={() => toast.dismiss(id)}
        closeLabel="Close banner"
        action={
          action
            ? {
                label: action.label,
                onClick: () => {
                  action.onClick();
                  toast.dismiss(id);
                },
              }
            : undefined
        }
      >
        {description ?? message}
      </Banner>
    ),
    { duration: Infinity },
  );
}

export { banner };

button.source:
import * as React from "react";
import { Slot } from "@radix-ui/react-slot";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const buttonVariants = cva(
  "h-fit inline-flex rounded-button border cursor-pointer items-center justify-center gap-2 whitespace-nowrap text-sm font-medium transition-all hover:shadow-[0px_2px_5px_0px_#40445214,0px_0px_0px_4px_#4362835C,0px_0px_0px_1px_#40445229,0px_1px_1px_0px_#0000001F] focus-visible:shadow-[0px_1px_1px_0px_#0000001F,0px_2px_5px_0px_#3C425714,0px_3px_9px_0px_#3C425714] disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg:not([class*='size-'])]:size-4 shrink-0 [&_svg]:shrink-0 outline-none aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive",
  {
    variants: {
      variant: {
        primary: "bg-surface text-surface-foreground  border-border-neutral",
        secondary: "bg-brand text-white border-brand",
        destructive: "bg-destructive text-white border-destructive",
      },
      size: {
        sm: "px-2 py-1 gap-1.5 px-2 text-xs has-[>svg]:px-1.5 [&_svg:not([class*='size-'])]:size-3",
        md: "px-2 py-1 gap-1.5 px-3 has-[>svg]:px-2.5",
        lg: "px-4 py-2 gap-2 px-6 has-[>svg]:px-4",
      },
    },
    defaultVariants: {
      variant: "primary",
      size: "sm",
    },
  },
);

function Button({
  className,
  variant = "primary",
  size = "md",
  asChild = false,
  ...props
}: React.ComponentProps<"button"> &
  VariantProps<typeof buttonVariants> & {
    asChild?: boolean;
  }) {
  const Comp = asChild ? Slot : "button";

  return (
    <Comp
      data-slot="button"
      data-variant={variant}
      data-size={size}
      className={cn(buttonVariants({ variant, size, className }))}
      {...props}
    />
  );
}

export { Button, buttonVariants };

