  if (!label && !description) {
    return checkbox
  }

  return (
    <div className="flex items-start gap-2">
      {checkbox}
      <div className="grid gap-1.5 leading-none">
        {label && (
          <label
            htmlFor={id}
            className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70"
          >
