link.source:
import { cva, type VariantProps } from "class-variance-authority";
import Link from "next/link";
import * as React from "react";

import { cn } from "@/lib/utils";

const linkVariants = cva(
  "inline-flex items-center gap-1 text-sm font-medium transition-colors disabled:pointer-events-none disabled:opacity-50 focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring/50 [aria-disabled=true]:pointer-events-none [aria-disabled=true]:opacity-50 [aria-disabled=true]:text-link-disabled [aria-disabled=true]:hover:text-link-disabled",
  {
    variants: {
      variant: {
        primary: "text-primary hover:text-primary/80 no-underline",
        secondary:
          "text-link-secondary underline decoration-dotted underline-offset-4 hover:text-link-secondary/80",
      },
    },
    defaultVariants: {
      variant: "primary",
    },
  }
);

export interface AppLinkProps
  extends React.ComponentPropsWithoutRef<typeof Link>,
    VariantProps<typeof linkVariants> {
  disabled?: boolean;
}

const AppLink = React.forwardRef<HTMLAnchorElement, AppLinkProps>(
  ({ className, variant, disabled, href, onClick, ...props }, ref) => {
    const isDisabled =
      typeof disabled === "string" ? disabled !== "false" : Boolean(disabled);

    if (isDisabled) {
      return (
        <span
          role="link"
          aria-disabled="true"
          className={cn(
            linkVariants({ variant }),
            "cursor-not-allowed text-link-disabled opacity-60",
            className
          )}
          tabIndex={-1}
          {...props}
        />
      );
    }

    return (
      <Link
        ref={ref}
        className={cn(linkVariants({ variant }), className)}
        href={href ?? "#"}
        onClick={onClick}
        {...props}
      />
    );
  }
);
AppLink.displayName = "AppLink";

export { AppLink, linkVariants };

list-view.source:
"use client"

import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const listItemVariants = cva(
  "flex w-full min-w-0 gap-3 border-b border-border py-2.5 text-left last:border-b-0",
  {
    variants: {
      variant: {
        iconTitleSecondaryValue: "items-start",
        titleSecondaryValue: "items-start",
        iconTitleValue: "items-center",
        titleValue: "items-center",
        valueOnly: "items-center",
      },
      withBackground: {
        true: "rounded-lg bg-[#F9F6F1] px-3 dark:bg-muted/50",
        false: "",
      },
    },
    defaultVariants: {
      variant: "iconTitleSecondaryValue",
      withBackground: false,
    },
  }
)

export interface ListItemProps
  extends Omit<React.HTMLAttributes<HTMLDivElement>, "title">,
    VariantProps<typeof listItemVariants> {
  icon?: React.ReactNode
  title?: React.ReactNode
  secondary?: React.ReactNode
  value?: React.ReactNode
  asChild?: boolean
}

const ListItem = React.forwardRef<HTMLDivElement, ListItemProps>(
  (
    {
      className,
      variant = "iconTitleSecondaryValue",
      withBackground = false,
      icon,
      title,
      secondary,
      value,
      children,
      ...props
    },
    ref
  ) => {
    const showIcon =
      variant === "iconTitleSecondaryValue" || variant === "iconTitleValue"
    const showTitle =
      variant === "iconTitleSecondaryValue" ||
      variant === "titleSecondaryValue" ||
      variant === "iconTitleValue" ||
      variant === "titleValue"
    const showSecondary =
      variant === "iconTitleSecondaryValue" || variant === "titleSecondaryValue"
    const showValue =
      variant === "iconTitleSecondaryValue" ||
      variant === "titleSecondaryValue" ||
      variant === "iconTitleValue" ||
      variant === "titleValue" ||
      variant === "valueOnly"

    const hasMainBlock = showTitle || showSecondary

    return (
      <div
        ref={ref}
        data-slot="list-item"
        data-variant={variant}
        data-with-background={withBackground}
        className={cn(listItemVariants({ variant, withBackground }), className)}
        {...props}
      >
        {showIcon && icon != null && (
          <span className="flex shrink-0 text-muted-foreground [&>svg]:size-5">
            {icon}
          </span>
        )}
        {hasMainBlock && (
          <div className="flex min-w-0 flex-1 flex-col gap-0.5">
            {showTitle && title != null && (
              <span className="truncate text-sm font-medium text-foreground">
                {title}
              </span>
            )}
            {showSecondary && secondary != null && (
              <span className="truncate text-xs text-muted-foreground">
                {secondary}
              </span>
            )}
          </div>
        )}
        {showValue && value != null && (
          <span
            className={cn(
              "shrink-0 text-sm text-muted-foreground",
              hasMainBlock && "text-right"
            )}
          >
            {value}
          </span>
        )}
        {children}
      </div>
    )
  }
)
ListItem.displayName = "ListItem"

function ListView({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) {
  return (
    <div
      data-slot="list-view"
      role="list"
      className={cn("flex flex-col", className)}
      {...props}
    />
  )
}
ListView.displayName = "ListView"

export interface ListProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Optional header content (e.g. title) rendered above the list */
  header?: React.ReactNode
}

const List = React.forwardRef<HTMLDivElement, ListProps>(
  ({ className, header, children, ...props }, ref) => {
    return (
      <div
        ref={ref}
        data-slot="list"
        className={cn("flex flex-col", className)}
        {...props}
      >
        {header != null && (
          <div className="mb-2 text-sm font-medium text-foreground">
            {header}
          </div>
        )}
        <ListView>{children}</ListView>
      </div>
    )
  }
)
List.displayName = "List"

export { List, ListItem, ListView, listItemVariants }

